import React from 'react';
import { useState, useEffect, useCallback  } from 'react';
import {
    AiTwotoneEdit,
    AiFillDelete,
} from 'react-icons/ai';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux'
import { removeComment } from '../redux/features/comment/commentSlice.js';
import { toast } from 'react-toastify'

import axios from '../utils/axios'


export const CommentItem = ({ cmt }) => {
    const avatar = cmt.comment.trim().toUpperCase().split('').slice(0, 2);


    const { user } = useSelector((state) => state.auth)
    const [comment, setComment] = useState('');
    const dispatch = useDispatch();
    const params = useParams();
    console.log({ params1: params })

    const removeCommentHandler = () => {
        try {
            console.log({a: dispatch(removeComment(params.id))})
            dispatch(removeComment(params.id))
            toast('Comment was deleted')
        } catch (error) {
            console.log(error)
        }
    }

    const fetchComment = useCallback(async () => {
        const { data } = await axios.get(`/posts/comments/${params.id}`)
        setComment(data)
    }, [params.id])

    useEffect(() => {
        fetchComment()
    }, [fetchComment])

    return (
        <div className='flex items-center gap-3'>
            <div className='flex items-center justify-center shrink-0 rounded-full w-10 h-10 bg-blue-300 text-sm'>
                {avatar}
            </div>
            <div className='flex text-gray-300 text-[10px] w-full'>{cmt.comment}</div>
            <div className='flex items-center gap-3'>
                {user?._id === comment.author && (
                    <div className='flex gap-3'>
                        <button className='flex items-center justify-center gap-2 text-white opacity-50'>
                            {/* <Link to={`/${params.id}/edit`}> */}
                         <AiTwotoneEdit onClick={setComment} />
                            {/* </Link> */}
                        </button>
                       <button
                            onClick={removeCommentHandler}
                            className='flex items-center justify-center gap-2  text-white opacity-50'
                        >
                            <AiFillDelete />
                        </button>
                    </div>
                )}
            </div> 


        </div>
    )
}
