import React, { useState } from "react";

export const AddPostPage = () => {
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [image, setImage] = useState('');

    const submitHandler = () => {
        try {
            const data = new FormData();
            data.append('title', title);
            data.append('text', text);
            data.append('image', image);
        } catch (error) {
            console.log(error)
        }
    }

    return (
    <form
        className="w-1/3 mx-auto py-10"
        onSubmit={(e) => e.preventDefault()}
    >
        <label className="text-gray-300 py-2 bg-gray-600 text-xs mt-2 flex items-center justify-center border-2 border-dotted cursor-pointer">
            Attach image:
            <input
                type='file'
                className="hidden"
                onChange={(e) => setImage(e.target.files[0])}
            >
            </input>
        </label>
        <div className="flex object-cover py-2">IMAGE</div>
        <label className="text-xs text-white opacity-70">
            Post title:
            <input
                type='text'
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Title"
                className="mt-1 text-black w-full rounded-lg bg-gray-400 border py-1 px-2 text-xs outline-none placeholder:text-gray-700"
            />
        </label>
        <label className="text-xs text-white opacity-70">
            Post text:
            <textarea
                placeholder="Text"
                onChange={(e) => setText(e.target.value)}
                className="mt-1 text-black w-full rounded-lg bg-gray-400 border py-1 px-2 text-xs resize-none h-40 outline-none placeholder:text-gray-700"
            />
        </label>
        <div className="flex gap-8 items-center justify-center mt-4">
            <button className="flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4">
                Add post
            </button>
            <button className="flex justify-center items-center bg-red-500 text-xs text-white rounded-sm py-2 px-4">
                Cencel
            </button>
        </div>
    </form>
)}
