import React, { useCallback, useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { AiFillEye, AiOutlineMessage, AiTwotoneEdit, AiTwotoneDelete } from 'react-icons/ai';
import Moment from 'react-moment';
import { toast } from 'react-toastify';
import axios from '../utils/axios.js';
import { useSelector, useDispatch } from 'react-redux';
import { removePost } from '../redux/features/post/postSlice.js';
import { createComment, getPostComments } from '../redux/features/comment/commentSlice.js';


export const PostPage = () => {
  const [post, setPost] = useState(null);
  const [comment, setComment] = useState('');
  const { user } = useSelector(state => state.auth);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const params = useParams();

  const removePostHandler = () => {
    try {
      dispatch(removePost(params.id));
      toast('Post was deleted');
      navigate('/posts');
    } catch (error) {
      console.log(error)
    }
  };

  const submitHandler = () => {
    try {
      const postId = params.id
      dispatch(createComment({postId, comment}));
      setComment('');
      toast('Comment was added');
    } catch (error) {
      console.log(error)
    }
  };

  const fetchComments = useCallback(async () => {
    try {
      dispatch(getPostComments(params.id))
    } catch (error) {
      console.log(error)
    }
  }, [dispatch, params.id]);


  const fetchPost = useCallback(async () => {
    const { data } = await axios.get(`/posts/${params.id}`)
    setPost(data)
  }, [params.id])

  useEffect(() => {
    fetchPost()
  }, [fetchPost]);

  useEffect(() => {
    fetchComments()
  }, [fetchComments])

  if (!post) {
    return (
      <div className='text-xl text-center text-white py-10'>Loading...</div>
    )
  }


  return (
    <div>
      <button className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4'>
        <Link className='flex' to={'/'}>
          Back
        </Link>
      </button>
      <div className='flex gap-10 py-8'>
        <div className='w-2/3'>
          <div className="flex flex-col basis-1/4 flex-grow">
            <div
              className={
                post?.imgUrl
                  ? 'flex rounded-sm h-80'
                  : 'flex rounded-sm'
              }
            >
              {post?.imgUrl && (
                <img
                  src={`http://localhost:3001/${post.imgUrl}`}
                  alt='img'
                  className='object-cover w-full'
                />
              )}
            </div>
            <div className='flex justify-between items-center pt-2'>
              <div className='text-xs text-white opacity-50'>{post.username}</div>
              <div className='text-xs text-white opacity-50'>
                <Moment post={post.createdAt} format='D MMM YYYY' />
              </div>
            </div>
            <div className='text-white text-xl'>{post.title}</div>
            <p className='text-white opacity-60 text-xs t-4'>{post.text}</p>
            <div className='flex gap-3 mt-2 items-center justify-between'>
              <div className='flex gap-3 mt-4'>
                <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                  <AiFillEye /><span>{post.views}</span>
                </button>
                <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                  <AiOutlineMessage /><span>{post.comments?.length || 0}</span>
                </button>
              </div>

            {
              user?._id === post.author && (
                <div className='flex gap-3 mt-4'>
                  <button className='flex items-center justify-center gap-2 text-white opacity-50'>
                   <Link to={`/${params.id}/edit`}>
                      <AiTwotoneEdit />
                   </Link>
                  </button>
                  <button
                    onClick={removePostHandler}
                    className='flex items-center justify-center gap-2 text-white opacity-50'>
                    <AiTwotoneDelete />
                  </button>
                </div>
              )
            }
            </div>
          </div>
        </div>
        <div className='w-1/3 p-8 bg-gray-700 flex flex-col gap-2 rounded-sm'>
          <form className='flex gap-2' onSubmit={(e) => e.preventDefault()}>
            <input
              type='text'
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder='comment'
              className='text-black w-full rounded-sm bg-gray-400 border p-2 text-xs outline-none placeholder:text-gray-700'
            />
            <button
              type='submit'
              onClick={submitHandler}
              className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4'
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  )
}
