import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export const RegisterPage = () => {
  const [username, setUserName] = useState('');
  const [password, setPassword] = useState('');
  return (
    <form onSubmit={(e) => e.preventDefault()} className='w-1/4 h-60 mx-auto mt-40'>
      <h1 className='text-lg text-white text-center'>Registration</h1>
      <label className='text-xs text-gray-400'>
        Username:
        <input
          type='text'
          value={username}
          onChange={(e) => setUserName(e.target.value)}
          placeholder='Username'
          className='mt-1 text-black w-full rounded-lg bg-gray-400 border py-1 px-2 text-xs outline-none placeholder:text-gray-700'
        />
      </label>
      <label className='text-xs text-gray-400'>
        Password:
        <input
          type='password'
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder='Password'
          className='mt-1 text-black w-full rounded-lg bg-gray-400 border py-1 px-2 text-xs outline-none placeholder:text-gray-700'
        />
      </label>
      <div className='flex gap-8 justify-center mt-4'>
        <button className='flex justify-center items-center text-xs text-white  bg-gray-600 rounded-sm py-2 px-4'>Sign up</button>
        <Link
          to={'/login'}
          className='flex justify-center items-center text-xs text-white'>
          Already have an account? sign in
        </Link>
      </div>
    </form>
  )
}
